import { Component, OnInit } from '@angular/core';
import { CountriesService } from './services/countries.service';
import { CountryMapper } from './mappers/country.mapper';
import { Country } from './models/country.model';
import { Store } from '@ngrx/store';
import { AppState } from './state/AppState';
import { highlightRegion } from './state/country.actions';
import { Observable, of } from 'rxjs';

@Component({
  selector: 'app-root',
  // template: `
  //   <button (click)="onHover('Europe')">Click</button>
  //   <div>Highlighted Region: {{ highlightedRegion$ | async }}</div>
  // `,
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
// export class AppComponent {
//   highlightedRegion$: Observable<string>;

//   constructor(private store: Store<AppState>) {
//     this.highlightedRegion$ = store.select('highlightedRegion');
//     // this.highlightedRegion$ = this.store.select(selectHighlightedRegion);
//     this.highlightedRegion$.subscribe((region) => console.log(region));
//   }

//   onHover(region: string) {
//     this.store.dispatch(highlightRegion({ region }));
//   }
// }
export class AppComponent implements OnInit {
  title = 'countries-app';
  regions = ['Europe', 'Asia'];
  countries: Array<Country> = [];
  highlightedRegion$: Observable<string>;

  constructor(
    private countriesService: CountriesService,
    private store: Store<AppState>
  ) {
    this.highlightedRegion$ = store.select('highlightedRegion');
  }

  ngOnInit() {
    this.countriesService.fetchAllCountries().subscribe((res) => {
      this.countries = CountryMapper.fromCountryDtoToCountry(res);
    });
    this.highlightedRegion$.subscribe((region) => console.log(region));
  }

  onHover(region: string) {
    this.store.dispatch(highlightRegion({ region }));
  }
}
<div class="page-layout">
  {{ highlightedRegion$ | async | json }}
  <div class="regions-container">
    <mat-card class="region-card" *ngFor="let region of regions">
      <div
        class="region-title"
        [class.highlighted]="(highlightedRegion$ | async) === region"
      >
        {{ region }}
      </div>
    </mat-card>
  </div>

  <div class="cards-container">
    <mat-card
      *ngFor="let country of countries"
      (mouseenter)="onHover(country.region)"
    >
      <mat-card-content>
        <div>
          <img [src]="country.flag" />
        </div>
        <div>
          {{ country.name }}
        </div>
      </mat-card-content>
    </mat-card>
  </div>
</div>
