import { Component, OnInit } from '@angular/core';
import { CountriesService } from './services/countries.service';
import { CountryMapper } from './mappers/country.mapper';
import { Country } from './models/country.model';
import { Store } from '@ngrx/store';
import { AppState } from './state/AppState';
import { highlightRegion } from './state/country.actions';
import { Observable, of } from 'rxjs';

@Component({
  selector: 'app-root',
  template: `
    <button (click)="onHover('Europe')">Click</button>
    <div>Highlighted Region: {{ highlightedRegion$ | async }}</div>
  `,
  // templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  highlightedRegion$: Observable<string>;

  constructor(private store: Store<AppState>) {
    this.highlightedRegion$ = this.store.select('highlightedRegion');
  }

  onHover(region: string) {
    this.store.dispatch(highlightRegion({ region }));
  }
}
